var makeJsonFromTable=function(a)
{
    var b=$("#"+a),
        c=$(b).find("thead"),
        d=$(b).find("tbody"),
        e=$(b).find("tbody>tr").length,f=[],g=[];
    $.each($(c).find("tr>th"),function(a,b)
    {
        f.push($(b).text())
    }),
    $.each($(d).find("tr"),function(a,b)
    {
        for(var c={} , d=0;d<f.length;d++)
            c[f[d]]=$(this).find("td").eq(d).text();
            g.push(c)
    });
    var h={};
    return h.count=e,h.value=g,h;
};

